import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faEdit } from "@fortawesome/free-solid-svg-icons";

const AssetTable = ({
	assets,
	onDeleteAsset,
	onEditAsset,
	activeAssetIDs,
	setActiveAssetIDs,
}) => {
	const [selectedImage, setSelectedImage] = useState(null);
	const [selectedAsset, setSelectedAsset] = useState(null);

	const handleBorrowClick = (assetID) => {
		setActiveAssetIDs((prevIDs) =>
			prevIDs.includes(assetID)
				? prevIDs.filter((id) => id !== assetID)
				: [...prevIDs, assetID]
		);
	};

	const handleImageClick = (image) => {
		setSelectedImage(image);
	};

	const handleCloseImageModal = () => {
		setSelectedImage(null);
	};

	const handleAssetNameClick = (asset) => {
		setSelectedAsset(asset);
	};

	const handleCloseAssetModal = () => {
		setSelectedAsset(null);
	};

	return (
		<div className="relative p-4 w-full bg-white border border-gray-200 rounded-lg shadow-md font-roboto text-[20px]">
			<table className="table-auto w-full border-collapse">
				<thead>
					<tr>
						<th className="text-[20px] font-medium">ID</th>
						<th className="text-[20px] font-medium">Date Created</th>
						<th className="text-[20px] font-medium">Asset</th>
						<th className="text-[20px] font-medium">Category</th>
						<th className="text-[20px] font-medium">Location</th>
						<th className="text-[20px] font-medium">Quantity</th>
						<th className="text-[20px] font-medium">Cost</th>
						<th className="text-[20px] font-medium">Borrow</th>
						<th className="text-[20px] font-medium">Actions</th>
					</tr>
				</thead>
				<tbody className="text-center">
					{assets.map((asset) => (
						<tr key={asset.assetID}>
							<td>{asset.assetID}</td>
							<td>{asset.createdDate}</td>
							<td>
								<div className="flex items-center text-center">
									{asset.image && (
										<img
											src={asset.image}
											alt={asset.assetName}
											className="h-8 w-8 object-cover mr-2 cursor-pointer"
											onClick={() => handleImageClick(asset.image)}
										/>
									)}
									<span
										className="cursor-pointer text-blue-600 hover:underline"
										onClick={() => handleAssetNameClick(asset)}
									>
										{asset.assetName}
									</span>
								</div>
							</td>
							<td>{asset.selectedCategory}</td>
							<td>{asset.selectedLocation}</td>
							<td>{asset.quantity}</td>
							<td>{`₱${asset.cost}`}</td>
							<td>
								<div className="flex items-center">
									<button
										className={`w-4 h-4 rounded-full border-2 border-gray-400 ${
											activeAssetIDs.includes(asset.assetID)
												? "bg-blue-500"
												: "bg-white"
										} shadow-md transition-transform transform ${
											activeAssetIDs.includes(asset.assetID)
												? "scale-110"
												: "scale-100"
										}`}
										onClick={() => handleBorrowClick(asset.assetID)}
										aria-label={`Borrow ${asset.assetID}`}
									></button>
								</div>
							</td>
							<td>
								<div className="flex items-center space-x-2">
									<div className="flex items-center">
										<button
											className="text-black-600 flex items-center space-x-1"
											onClick={() => onEditAsset(asset)}
										>
											<FontAwesomeIcon icon={faEdit} />
											<span></span>
										</button>
									</div>
									<div className="flex items-center">
										<button
											className="text-black-600 flex items-center space-x-1"
											onClick={() => onDeleteAsset(asset.assetID)}
										>
											<FontAwesomeIcon icon={faTrash} />
											<span></span>
										</button>
									</div>
								</div>
							</td>
						</tr>
					))}
				</tbody>
			</table>

			{/* Modal for enlarged image */}
			{selectedImage && (
				<div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-75">
					<div className="relative bg-white p-4 rounded-lg shadow-lg">
						<img
							src={selectedImage}
							alt="Enlarged Asset"
							className="h-96 w-96 object-cover"
						/>
						<button
							className="absolute top-2 right-2 text-gray-600"
							onClick={handleCloseImageModal}
						>
							Close
						</button>
					</div>
				</div>
			)}

			{/* Modal for asset details */}
			{selectedAsset && (
				<div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-75">
					<div className="relative bg-white p-6 rounded-lg shadow-lg w-[500px]">
						<h2 className="text-[20px] font-semibold mb-4">
							{selectedAsset.assetName}
						</h2>
						{selectedAsset.image && (
							<img
								src={selectedAsset.image}
								alt={selectedAsset.assetName}
								className="h-32 w-32 object-cover mb-4"
							/>
						)}
						<p>
							<strong>ID:</strong> {selectedAsset.assetID}
						</p>
						<p>
							<strong>Date Created:</strong> {selectedAsset.createdDate}
						</p>
						<p>
							<strong>Details:</strong> {selectedAsset.assetDetails}
						</p>
						<p>
							<strong>Category:</strong> {selectedAsset.selectedCategory}
						</p>
						<p>
							<strong>Location:</strong> {selectedAsset.selectedLocation}
						</p>
						<p>
							<strong>Quantity:</strong> {selectedAsset.quantity}
						</p>
						<p>
							<strong>Cost:</strong> ₱{selectedAsset.cost}
						</p>
						<button
							className="absolute top-2 right-2 text-gray-600"
							onClick={handleCloseAssetModal}
						>
							Close
						</button>
					</div>
				</div>
			)}
		</div>
	);
};

export default AssetTable;
