import React, { useState, useCallback } from "react";
import AddCategory from "./addcategory";

const Modal = ({
	isOpen,
	onClose,
	assetID,
	categories,
	selectedCategory,
	setSelectedCategory,
	assetName,
	setAssetName,
	assetDetails,
	setAssetDetails,
	quantity,
	setQuantity,
	cost,
	setCost,
}) => {
	if (!isOpen) return null;

	const handleAddAsset = () => {
		setAssetName("");
		setAssetDetails("");
		setQuantity(1);
		setCost("");
		setSelectedCategory("");
		onClose();
	};

	return (
		<div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
			<div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
				<h2 className="text-lg font-semibold mb-4">Add Asset</h2>

				<InputField label="Asset ID" value={assetID} readOnly />
				<SelectField
					label="Category"
					value={selectedCategory}
					onChange={(e) => setSelectedCategory(e.target.value)}
					options={categories}
				/>
				<InputField
					label="Asset Name"
					value={assetName}
					onChange={(e) => setAssetName(e.target.value)}
				/>
				<InputField
					label="Asset Details"
					value={assetDetails}
					onChange={(e) => setAssetDetails(e.target.value)}
				/>
				<InputField
					label="Quantity"
					type="number"
					value={quantity}
					onChange={(e) => setQuantity(Number(e.target.value))}
				/>
				<InputField
					label="Cost"
					value={cost}
					onChange={(e) => setCost(e.target.value.replace(/[^0-9]/g, ""))}
					prefix="â‚±"
				/>

				<div className="flex justify-end gap-4 mt-4">
					<Button className="bg-gray-300" onClick={onClose}>
						Cancel
					</Button>
					<Button className="bg-green-500 text-white" onClick={handleAddAsset}>
						Add Asset
					</Button>
				</div>
			</div>
		</div>
	);
};

const InputField = ({
	label,
	value,
	onChange,
	type = "text",
	readOnly = false,
	prefix,
}) => (
	<div className="mb-4 relative">
		<label className="block text-sm font-medium mb-2">{label}</label>
		<input
			type={type}
			value={value}
			onChange={onChange}
			readOnly={readOnly}
			className={`border p-2 rounded w-full ${prefix ? "pl-8" : ""}`}
			placeholder={prefix ? "" : label}
		/>
		{prefix && (
			<span className="absolute left-2 top-1/2 transform -translate-y-2/2 text-black-500">
				{prefix}
			</span>
		)}
	</div>
);

const SelectField = ({ label, value, onChange, options }) => (
	<div className="mb-4">
		<label className="block text-sm font-medium mb-2">{label}</label>
		<select
			value={value}
			onChange={onChange}
			className="border p-2 rounded w-full"
		>
			<option value="">Select Category</option>
			{options.length > 0 ? (
				options.map((category, index) => (
					<option key={index} value={category}>
						{category}
					</option>
				))
			) : (
				<option value="">No categories available</option>
			)}
		</select>
	</div>
);

const Button = ({ className, onClick, children }) => (
	<button className={`px-4 py-2 rounded-lg ${className}`} onClick={onClick}>
		{children}
	</button>
);

const AddAsset = () => {
	const [isModalOpen, setIsModalOpen] = useState(false);
	const [assetID, setAssetID] = useState(1); // Start with ID 1
	const [assetName, setAssetName] = useState("");
	const [assetDetails, setAssetDetails] = useState("");
	const [quantity, setQuantity] = useState(1);
	const [cost, setCost] = useState("");
	const [selectedCategory, setSelectedCategory] = useState("");
	const [categories, setCategories] = useState([]);

	const handleOpenModal = useCallback(() => {
		setIsModalOpen(true);
	}, []);

	const handleCloseModal = useCallback(() => {
		setIsModalOpen(false);
	}, []);

	const handleAddCategory = useCallback((newCategory) => {
		setCategories((prevCategories) => [...prevCategories, newCategory]);
	}, []);

	return (
		<div>
			<Button
				className="bg-[#68C231] text-[#343B3F] text-[10px] font-semibold"
				onClick={handleOpenModal}
			>
				Add Asset
			</Button>
			<Modal
				isOpen={isModalOpen}
				onClose={handleCloseModal}
				onSave={() => {}}
				assetID={assetID}
				setAssetID={setAssetID}
				categories={categories}
				selectedCategory={selectedCategory}
				setSelectedCategory={setSelectedCategory}
				assetName={assetName}
				setAssetName={setAssetName}
				assetDetails={assetDetails}
				setAssetDetails={setAssetDetails}
				quantity={quantity}
				setQuantity={setQuantity}
				cost={cost}
				setCost={setCost}
			/>
			<AddCategory onSaveCategory={handleAddCategory} />{" "}
			{/* Add the AddCategory component */}
		</div>
	);
};

export default AddAsset;
