import React, { useState } from "react";

const AddAsset = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [assetID, setAssetID] = useState(1); // Start with ID 1
  const [assetName, setAssetName] = useState("");
  const [assetDetails, setAssetDetails] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [cost, setCost] = useState("");
  const [categories, setCategories] = useState([]); // Initialize as empty array
  const [selectedCategory, setSelectedCategory] = useState("");
  const [newCategory, setNewCategory] = useState("");
  const [isAddCategoryModalOpen, setIsAddCategoryModalOpen] = useState(false);

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleAddCategory = () => {
    if (newCategory && !categories.includes(newCategory)) {
      setCategories((prevCategories) => [...prevCategories, newCategory]);
      setNewCategory("");
      setIsAddCategoryModalOpen(false);
    }
  };

  const handleAddAsset = () => {
    // Logic to handle asset addition
    setAssetID((prevID) => prevID + 1);
    setAssetName("");
    setAssetDetails("");
    setQuantity(1);
    setCost("");
    setSelectedCategory(""); // Clear the selected category
    handleCloseModal();
  };

  return (
    <div>
      <button
        className="flex w-[112px] h-[41px] p-4 justify-center items-center gap-4 flex-shrink-0 rounded-lg bg-[#68C231] text-[#343B3F] font-roboto text-[10px] font-semibold"
        onClick={handleOpenModal}
      >
        Add Asset
      </button>

      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
            <h2 className="text-lg font-semibold mb-4">Add Asset</h2>

            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Asset ID</label>
              <input
                type="text"
                value={assetID}
                readOnly
                className="border p-2 rounded w-full bg-gray-100"
              />
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Category</label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="border p-2 rounded w-full"
              >
                <option value="">Select Category</option>
                {categories.map((category, index) => (
                  <option key={index} value={category}>
                    {category}
                  </option>
                ))}
              </select>
              <button
                className="mt-2 text-blue-500 underline"
                onClick={() => setIsAddCategoryModalOpen(true)}
              >
                Add New Category
              </button>
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Asset Name</label>
              <input
                type="text"
                placeholder="Asset Name"
                value={assetName}
                onChange={(e) => setAssetName(e.target.value)}
                className="border p-2 rounded w-full"
              />
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Asset Details</label>
              <input
                type="text"
                placeholder="Asset Details"
                value={assetDetails}
                onChange={(e) => setAssetDetails(e.target.value)}
                className="border p-2 rounded w-full"
              />
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Quantity</label>
              <input
                type="number"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
                className="border p-2 rounded w-full"
              />
            </div>

            <div className="mb-4 relative">
              <label className="block text-sm font-medium mb-2">Cost</label>
              <input
                type="text"
                placeholder=""
                value={cost}
                onChange={(e) => setCost(e.target.value.replace(/[^0-9]/g, ""))} // Allow only numeric input
                className="border p-2 rounded w-full pl-8"
              />
              <span className="absolute left-2 top-1/2 transform -translate-y-2/2 text-black-500">
                â‚±
              </span>
            </div>

            <div className="flex justify-end gap-4">
              <button
                className="bg-gray-300 px-4 py-2 rounded-lg"
                onClick={handleCloseModal}
              >
                Cancel
              </button>
              <button
                className="bg-green-500 px-4 py-2 text-white rounded-lg"
                onClick={handleAddAsset}
              >
                Add Asset
              </button>
            </div>
          </div>
        </div>
      )}

      {isAddCategoryModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
            <h2 className="text-lg font-semibold mb-4">Add Category</h2>
            <input
              type="text"
              placeholder="Category Name"
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
              className="border p-2 rounded mb-4 w-full"
            />
            <div className="flex justify-end gap-4">
              <button
                className="bg-gray-300 px-4 py-2 rounded-lg"
                onClick={() => setIsAddCategoryModalOpen(false)}
              >
                Cancel
              </button>
              <button
                className="bg-blue-500 px-4 py-2 text-white rounded-lg"
                onClick={handleAddCategory}
              >
                Add Category
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AddAsset;
