import React from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPrint } from "@fortawesome/free-solid-svg-icons";
import moment from 'moment';

const PrintAssetsPDF = ({ assets }) => {
  const handlePrint = () => {
    const printContent = document.getElementById('printable-asset-list').innerHTML;
    const originalContent = document.body.innerHTML;

    document.body.innerHTML = printContent;
    window.print();
    document.body.innerHTML = originalContent;
  };

  return (
    <div>
      <button
        onClick={handlePrint}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center"
      >
        <FontAwesomeIcon icon={faPrint} className="mr-2" />
        Print Asset Inventory
      </button>

      <div id="printable-asset-list" style={{ display: 'none' }}>
        <h1 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '20px', textAlign: 'center' }}>Asset Inventory</h1>
        {assets.map((asset) => (
          <div key={asset.asset_id} style={{ marginBottom: '20px', borderBottom: '1px solid #ccc', paddingBottom: '10px' }}>
            <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '10px' }}>{asset.assetName}</h2>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
              <div>
                <p><strong>ID:</strong> {asset.asset_id}</p>
                <p><strong>Date Created:</strong> {moment(asset.createdDate).format('MM/DD/YYYY')}</p>
                <p><strong>Quantity:</strong> {asset.quantity}</p>
                <p><strong>Status:</strong> {asset.is_active ? 'Active' : 'Inactive'}</p>
              </div>
              <div>
                <p><strong>Category:</strong> {asset.category}</p>
                <p><strong>Location:</strong> {asset.location}</p>
                <p><strong>Cost:</strong> â‚±{asset.cost}</p>
                <p><strong>Type:</strong> {asset.type}</p>
              </div>
            </div>
            <p><strong>Details:</strong> {asset.assetDetails}</p>
            {asset.image && (
              <img src={asset.image} alt={asset.assetName} style={{ maxWidth: '200px', margin: '10px auto', display: 'block' }} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PrintAssetsPDF;
